sys_script_client.script:
function onLoad() {
	jslog("Needit on-load: starting..");

	if(g_form.isNewRecord()){
		g_form.setValue('u_requested_for', g_user.userID);
		jslog("Needit on-load: setting user full name = " + g_user.getFullName());
	}
	try {
		doesntExist();
	}
	catch (err) {
		jslog("Needit on-load caught js error: " + err.message);
	}
	console.log("This message was written by console.log()");
}
